import { ethers } from 'ethers';
import fundAbi from '../fund_abi.json';

const FUND_ADDRESS = "0xeD1DB453C3156Ff3155a97AD217b3087D5Dc5f6E";
const RPC_URL = "http://127.0.0.1:8545";
const CHAIN_ID = parseInt(process.env.REACT_APP_CHAIN_ID || '31337');

// Initialize provider and contract
let provider;
let fundContract;

export const initWeb3 = async () => {
  try {
    // Initialize provider
    provider = new ethers.providers.JsonRpcProvider(RPC_URL, CHAIN_ID);

    // Debug: Check if we can connect to the network
    const network = await provider.getNetwork();
    console.log('Connected to network:', network);

    // Debug: Check if the contract exists at the address
    const code = await provider.getCode(FUND_ADDRESS);
    console.log('Contract code at address:', code.length > 2 ? 'Contract exists' : 'No contract found');

    // Initialize contract
    fundContract = new ethers.Contract(FUND_ADDRESS, fundAbi, provider);

    return { provider, fundContract };
  } catch (error) {
    console.error('Error initializing web3:', error);
    throw error;
  }
};

export const getFundData = async () => {
  try {
    if (!fundContract) {
      await initWeb3();
    }

    // Get contract data
    let totalSupply = ethers.BigNumber.from(0);
    let totalShares = ethers.BigNumber.from(0);
    let owner = 'Unknown';
    let tokenAddress = 'Unknown';

    try {
      totalSupply = await fundContract.totalSupply();
    } catch (e) {
      console.warn('totalSupply function not found in contract');
    }

    try {
      totalShares = await fundContract.totalShares();
    } catch (e) {
      console.warn('totalShares function not found in contract');
    }

    try {
      owner = await fundContract.owner();
    } catch (e) {
      console.warn('owner function not found in contract');
    }

    try {
      tokenAddress = await fundContract.token();
    } catch (e) {
      console.warn('token function not found in contract');
    }

    // Format data
    return {
      totalSupply: ethers.utils.formatEther(totalSupply),
      totalShares: ethers.utils.formatEther(totalShares),
      owner,
      tokenAddress,
    };
  } catch (error) {
    console.error('Error getting fund data:', error);
    return {
      totalSupply: '0',
      totalShares: '0',
      owner: 'Unknown',
      tokenAddress: 'Unknown',
    };
  }
};

export const getUserShares = async (userAddress) => {
  try {
    if (!fundContract) {
      await initWeb3();
    }

    const shares = await fundContract.getShares(userAddress);
    return ethers.utils.formatEther(shares);
  } catch (error) {
    console.error('Error getting user shares:', error);
    return '0';
  }
};

// Function to listen for events
export const getTransactionHistory = async () => {
  try {
    if (!fundContract) {
      await initWeb3();
    }

    // Get past events
    let depositEvents = [];
    let withdrawEvents = [];

    try {
      // Try a different approach to get events
      const filter = {
        address: FUND_ADDRESS,
        fromBlock: 0,
        toBlock: 'latest'
      };

      const logs = await provider.getLogs(filter);
      console.log('Found logs:', logs.length);

      // Process logs to find deposit and withdrawal events
      for (const log of logs) {
        // Check event signatures
        if (log.topics[0] === ethers.utils.id("sharesMinted(address,uint256)")) {
          depositEvents.push(log);
        } else if (log.topics[0] === ethers.utils.id("withdrawnShares(address,uint256)")) {
          withdrawEvents.push(log);
        }
      }
    } catch (e) {
      console.warn('Error getting deposit events:', e);
    }

    // Format events
    const deposits = depositEvents.map(event => ({
      type: 'deposit',
      user: '0x' + event.topics[1].slice(26),  // Extract address from topic
      amount: ethers.utils.formatEther(ethers.BigNumber.from(event.data)),
      timestamp: new Date(event.blockNumber * 1000), // Approximate timestamp
      transactionHash: event.transactionHash,
    }));

    const withdrawals = withdrawEvents.map(event => ({
      type: 'withdrawal',
      user: '0x' + event.topics[1].slice(26),  // Extract address from topic
      amount: ethers.utils.formatEther(ethers.BigNumber.from(event.data)),
      timestamp: new Date(event.blockNumber * 1000), // Approximate timestamp
      transactionHash: event.transactionHash,
    }));

    // Combine and sort by block number (descending)
    return [...deposits, ...withdrawals].sort((a, b) => b.timestamp - a.timestamp);
  } catch (error) {
    console.error('Error getting transaction history:', error);
    return [];
  }
};
